for i, number in enumerate(lista):
    if i < 1 or i > len(lista) - 2:
        continue

    # 1. Make number a string
    # 1.5. Make a counter
    # 2. Loop over that string, taking one character at a time
    # 3. Make that character a num
    # 4. Add that num to the counter
    current_sum = 0
    for character in str(number):
        current_sum += int(character)

    prev_sum = 0
    for character in str(lista[i-1]):
        prev_sum += int(character)

    next_sum = 0
    for character in str(lista[i+1]):
        next_sum += int(character)

    if current_sum < prev_sum and current_sum < next_sum:
        print(number)

    if current_sum > prev_sum and current_sum > next_sum:
        print(number)
